import classNames from "classnames"
import { useRef } from "react"
import { InputCheckboxComponent } from "./types"

export const InputCheckbox: InputCheckboxComponent = ({ id, checked = false, disabled, onChange, transactionId  }) => {
  const { current: inputId } = useRef(`RampInputCheckbox-${id}`)

    // console.log("checked prop value:", checked);
    // console.log("disabled prop value:", disabled);
    // Added closing label tag after the <input> to ensure correct HTML structure and resolve issue with missing checkbox styling

  return (
    <div className="RampInputCheckbox--container" data-testid={inputId}>

      <label
          className={classNames("RampInputCheckbox--label", {
          "RampInputCheckbox--label-checked": checked,
          "RampInputCheckbox--label-disabled": disabled,

        })}
      >
      <input
          id={inputId}
          type="checkbox"
          className="RampInputCheckbox--input"
          checked={checked}
          disabled={disabled}
          onChange={() => {
              console.log("Checked value is", checked);
              if (transactionId !== undefined) {
                  onChange(transactionId, !checked); // Pass transactionId to the onChange handler
              }
        }}
      />
      </label>
    </div>

  )
}
